
UPDATE mail_template
SET content='<meta content="text/html; charset=utf-8" http-equiv="Content-Type" /><title>FogPanel- E-mail Verification</title><div id="main" style="width:600px; height:auto; margin:0 auto; padding:0;"><div class="mainarea" style="width:600px; height:auto; float:left; border:1px solid #ececec;"><span style="color: rgb(234, 88, 0); font-family: Arial, Helvetica, sans-serif; font-size: 21px; line-height: 1.6666666666666667;">Please verify your email address<br /> </span><span style="color: rgb(102, 102, 102); font-family: Arial, Helvetica, sans-serif; font-size: 17px; font-weight: bold; line-height: 1.6666666666666667;">Hello [userName] ,<br /> </span><span style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.6666666666666667;">There is just one more step before you can access the full range of cloud services from FogPanel.</span><div class="maincontent" style="width:550px; height:auto; float:left; margin:15px 0 0 22px; padding:0 0 15px 0;"><p style="font-family:Arial,Helvetica,sans-serif;font-size:13px;font-weight:normal;color:#333;margin:15px 0 0 0;padding:0;height:auto;width:550px">Your user name is [userName] </b>.</p><p style="font-family:Arial,Helvetica,sans-serif;font-size:13px;font-weight:normal;color:#333;margin:15px 0 0 0;padding:0;height:auto;width:550px">Please verify your email by clicking on <a href="[verifyLink]" style="color:#2c8bbc; font-weight:bold;">confirm</a> link: or [verifyLink]</p><p style="font-family:Arial,Helvetica,sans-serif;font-size:13px;font-weight:normal;color:#333;margin:15px 0 0 0;padding:0;height:auto;width:550px">Regards,<br />[signature] </p></div><br /></div></div>'
WHERE id= 1;

