            var containerNode = this.containerNode;
            
            var wizardStepsNode = domConstruct.create("div");
            wizardStepsNode.className = "wizard row-fluid fogPanelWizard";
            var wizardStepsHTML = "<ul class='wizard-steps' id='wizardSteps'>";
